import React, { useState, useEffect } from 'react';
import { patientService, ambulanceService } from '../services/api';

const MapView = () => {
  const [patients, setPatients] = useState([]);
  const [ambulances, setAmbulances] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      const [patientsRes, ambulancesRes] = await Promise.all([
        patientService.getAll(),
        ambulanceService.getAll()
      ]);
      setPatients(patientsRes.data);
      setAmbulances(ambulancesRes.data);
    } catch (error) {
      console.error('Error loading data:', error);
    } finally {
      setLoading(false);
    }
  };

  const getEmergencyLevelColor = (level) => {
    switch (level) {
      case 'critical': return 'bg-red-500';
      case 'moderate': return 'bg-orange-500';
      case 'stable': return 'bg-green-500';
      default: return 'bg-gray-500';
    }
  };

  const getAmbulanceStatusColor = (status) => {
    switch (status) {
      case 'on_mission': return 'bg-blue-500';
      case 'available': return 'bg-green-500';
      case 'maintenance': return 'bg-yellow-500';
      case 'offline': return 'bg-gray-500';
      default: return 'bg-gray-500';
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-lg text-gray-600">Loading map data...</div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Map View</h1>
        <p className="text-gray-600">Real-time ambulance and patient locations</p>
      </div>

      {/* Legend */}
      <div className="bg-white rounded-lg border p-4">
        <h3 className="font-semibold mb-3">Legend</h3>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          <div className="flex items-center">
            <div className="w-3 h-3 bg-red-500 rounded-full mr-2"></div>
            <span className="text-sm">Critical Patient</span>
          </div>
          <div className="flex items-center">
            <div className="w-3 h-3 bg-orange-500 rounded-full mr-2"></div>
            <span className="text-sm">Moderate Patient</span>
          </div>
          <div className="flex items-center">
            <div className="w-3 h-3 bg-green-500 rounded-full mr-2"></div>
            <span className="text-sm">Stable Patient</span>
          </div>
          <div className="flex items-center">
            <div className="w-3 h-3 bg-blue-500 rounded-full mr-2"></div>
            <span className="text-sm">Ambulance</span>
          </div>
        </div>
      </div>

      {/* Map Container (Simulated) */}
      <div className="bg-white rounded-lg border p-6">
        <div className="relative bg-gray-100 rounded-lg h-96 border-2 border-gray-300">
          {/* Simulated Map Background */}
          <div className="absolute inset-0 bg-gradient-to-br from-blue-50 to-green-50 rounded-lg">
            {/* Simulated roads */}
            <div className="absolute top-1/4 left-0 right-0 h-1 bg-gray-400"></div>
            <div className="absolute top-1/2 left-0 right-0 h-1 bg-gray-400"></div>
            <div className="absolute top-3/4 left-0 right-0 h-1 bg-gray-400"></div>
            <div className="absolute left-1/4 top-0 bottom-0 w-1 bg-gray-400"></div>
            <div className="absolute left-1/2 top-0 bottom-0 w-1 bg-gray-400"></div>
            <div className="absolute left-3/4 top-0 bottom-0 w-1 bg-gray-400"></div>
            
            {/* Hospital Marker */}
            <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
              <div className="bg-red-600 text-white p-3 rounded-full shadow-lg">
                <span className="font-bold">H</span>
              </div>
              <div className="text-center mt-2 text-sm font-medium">Hospital</div>
            </div>

            {/* Patient Markers */}
            {patients.map((patient, index) => {
              if (!patient.location) return null;
              
              // Simulate positions around the hospital
              const angle = (index / patients.length) * 2 * Math.PI;
              const distance = 100 + (Math.random() * 100);
              const left = 50 + Math.cos(angle) * distance / 4;
              const top = 50 + Math.sin(angle) * distance / 4;

              return (
                <div
                  key={patient._id}
                  className="absolute transform -translate-x-1/2 -translate-y-1/2"
                  style={{ left: `${left}%`, top: `${top}%` }}
                >
                  <div className={`w-4 h-4 ${getEmergencyLevelColor(patient.emergencyLevel)} rounded-full border-2 border-white shadow-lg`}></div>
                  <div className="text-xs bg-white px-1 rounded mt-1 whitespace-nowrap">
                    {patient.name}
                  </div>
                </div>
              );
            })}

            {/* Ambulance Markers */}
            {ambulances.map((ambulance, index) => {
              if (!ambulance.currentLocation) return null;
              
              // Simulate positions
              const angle = (index / ambulances.length) * 2 * Math.PI;
              const distance = 50 + (Math.random() * 80);
              const left = 50 + Math.cos(angle) * distance / 4;
              const top = 50 + Math.sin(angle) * distance / 4;

              return (
                <div
                  key={ambulance._id}
                  className="absolute transform -translate-x-1/2 -translate-y-1/2"
                  style={{ left: `${left}%`, top: `${top}%` }}
                >
                  <div className="text-2xl">ðŸš‘</div>
                  <div className="text-xs bg-white px-1 rounded mt-1 whitespace-nowrap">
                    {ambulance.ambulanceId}
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        <div className="mt-4 text-center text-gray-600">
          <p>This is a simulated map view. In production, this would integrate with Google Maps API.</p>
        </div>
      </div>

      {/* Data Summary */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <div className="bg-white rounded-lg border p-4 text-center">
          <div className="text-2xl font-bold text-blue-600">{ambulances.length}</div>
          <div className="text-sm text-gray-600">Active Ambulances</div>
        </div>
        <div className="bg-white rounded-lg border p-4 text-center">
          <div className="text-2xl font-bold text-green-600">
            {patients.filter(p => p.emergencyLevel === 'stable').length}
          </div>
          <div className="text-sm text-gray-600">Stable Patients</div>
        </div>
        <div className="bg-white rounded-lg border p-4 text-center">
          <div className="text-2xl font-bold text-red-600">
            {patients.filter(p => p.emergencyLevel === 'critical').length}
          </div>
          <div className="text-sm text-gray-600">Critical Patients</div>
        </div>
      </div>
    </div>
  );
};

export default MapView;
